import {useReducer} from 'react';
import Select from "react-select";
import {showTypes, theatres} from "./seenOnBway";

function AddAShow() {
    const starterState = {
        id: 1,
        show: "",
        showType: showTypes[0],
        theatre: theatres[0],
        seenOn: undefined,
        stars: 5,
    };
    
    const reducer = (state, action) => {
        switch (action.type) {
            case 'id':
                return {...state, input: action.value};
            case 'reset_form':
                return starterState;    
        }
    }

    const [showData, dispatch] = useReducer(reducer, starterState);

    const handleEntry = (e) => {
        dispatch({type: 'input', payload: e.target.value});
    };
    
    return (
    <form>
        <div className="NumberField">
            <label>Show Number:<br />
            <input 
                type="number"
                name="id"
                value={showData.id}
                onChange={handleEntry}
                placeholder="1" />
            </label>
        </div>
        <div className="TextField">
            <label>Show Name:<br />
            <input 
                type="text"
                name="show"
                value={showData.show} 
                onChange={handleEntry}
                placeholder="enter show name" />
            </label>
        </div>
        <div className="SelectField">
            <label>Show Type:<br />
            <Select
               name="showType"
               options={showTypes}
               value={showData.showType}
               onChange={handleEntry}
               defaultValue={showTypes[0]} />
            </label>
        </div>
        <div className="SelectField">
            <label>Theatre:<br />
            <Select
                name="theatre"
                options={theatres}
                value={showData.theatre}
                onChange={handleEntry}
                placeholder="Choose A Theatre"
                defaultValue={theatres[0]} />
            </label>
        </div>
        <div className="DateField">
            <label>Date Seen:<br />
            <input 
                type="date"
                name="seenOn"
                value={showData.seenOn}
                onChange={handleEntry}/>
            </label>
        </div>
        <div classname="RatingField">
            <label>Rating:<br />
            <input
                type="range"
                name="stars"
                max="5"
                value={showData.stars}
                onChange={handleEntry} />
            </label>
        </div>
        <input type="submit" value="submit" />
    </form>
    );
};
export default AddAShow;
