import {useReducer} from 'react';
import Select from "react-select";
import {showTypes, theatres} from "./seenOnBway";

function AddAShow() {
    const [newShow, addNewShow] = useState({
        id: 1,
        show: "",
        showType: showTypes[0],
        theatre: theatres[0],
        seenOn: new Date(),
        stars: 0,
    });

    const handleNumber = (e) => {
       addNewShow({...newShow, id: e.target.value})
    };

    const handleName = (e) => {
        addNewShow({...newShow, show: e.target.value})
     };

     const handleType = (e) => {
        addNewShow({...newShow, showType: e.target.value})
     };

     const handleTheatre = (e) => {
        addNewShow({...newShow, theatre: e.target.value})
     };

     const handleDate = (e) => {
        addNewShow({...newShow, seenOn: e.target.value})
     };

     const handleStars = (e) => {
        addNewShow({...newShow, stars: e.target.value})
     };

    return (
    <form>
        <div className="NumberField">
            <label>Show Number:<br />
            <input
                type="number"
                name= "id"
                value={newShow.id}
                onChange={handleNumber}
                placeholder="1" />
            </label>
        </div>
        <div className="TextField">
            <label>Show Name:<br />
            <input 
                type="text"
                name="show"
                value={newShow.show} 
                onChange={handleName}
                placeholder="enter show name" />
            </label>
        </div>
        <div className="SelectField">
            <label>Show Type:<br />
            <Select
               name="showType"
               options={showTypes}
               value={newShow.showType}
               onChange={handleType}
               defaultValue={showTypes[0]} />
            </label>
        </div>
        <div className="SelectField">
            <label>Theatre:<br />
            <Select
                name="theatre"
                options={theatres}
                value={newShow.theatre}
                onChange={handleTheatre}
                defaultValue={theatres[0]} />
            </label>
        </div>
        <div className="DateField">
            <label>Date Seen:<br />
            <Date
                type="date"
                name="seenOn"
                value={newShow.seenOn}
                onChange={handleDate}/>
            </label>
        </div>
        <div classname="RatingField">
            <label>Rating:<br />
            <input
                type="range"
                name="stars"
                max="5"
                value={newShow.stars}
                onChange={handleStars} />
            </label>
        </div>
        <input type="submit" value="submit" />
    </form>
    );
};
export default AddAShow;
